import React, { useState, useEffect } from 'react';
import { AuthForm } from './components/AuthForm';
import { TreeTable } from './components/TreeTable';
import { normalizeName, formatNameForDisplay } from './utils/normalizeName';
import { signUp, login, logout } from './services/auth';
import { searchByName } from './services/api';

function App() {
    const [name, setName] = useState('');
    const [data, setData] = useState(null);
    const [error, setError] = useState('');
    const [user, setUser] = useState(null);

    const handleSignUp = async (email, password, firstName, secondName, year) => {
        try {
            const user = await signUp(email, password, firstName, secondName, year);
            const fullName = `${firstName} ${secondName} - Class of ${year}`;
            const normalizedName = normalizeName(fullName);
            setName(normalizedName);
            setUser(user);
        } catch (err) {
            setError(err.message);
        }
    };

    const handleLogin = async (email, password, firstName, secondName, year) => {
        try {
            const user = await login(email, password, firstName, secondName, year);
            const fullName = `${firstName} ${secondName} - Class of ${year}`;
            const normalizedName = normalizeName(fullName);
            setName(normalizedName);
            setUser(user);
        } catch (err) {
            setError(err.message);
        }
    };

    const handleLogout = async () => {
        try {
            await logout();
            setUser(null);
            setName('');
            setData(null);
        } catch (err) {
            setError(err.message);
        }
    };

    useEffect(() => {
        if (name) {
            // Example: Fetch data from the backend
            const fetchData = async () => {
                try {
                    const response = await axios.get('http://localhost:5000/api/trees');
                    const data = response.data;

                    // Normalize the names in the table data
                    const normalizedData = data.map(row => ({
                        ...row,
                        name: normalizeName(row.name), // Normalize the name
                    }));

                    setData(normalizedData); // Update state with normalized data
                } catch (err) {
                    console.error('Error fetching data:', err);
                }
            };
            fetchData();
        }
    }, [name]);

    return (
        <div style={{ padding: '20px' }}>
            <h1>Torque Experiment Data</h1>

            {!user ? (
                <>
                    <AuthForm type="signup" onSubmit={handleSignUp} error={error} />
                    <AuthForm type="login" onSubmit={handleLogin} error={error} />
                </>
            ) : (
                <div>
                    <p>Welcome, {formatNameForDisplay(name)}!</p>
                    <button onClick={handleLogout}>Logout</button>

                    {data && <TreeTable data={data} />}
                </div>
            )}
        </div>
    );
}

export default App;
