import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { signInWithEmailAndPassword, createUserWithEmailAndPassword, signOut } from 'firebase/auth';
import { auth } from './firebase';

function normalizeName(name) {
    // Remove all punctuation marks and convert to lowercase
    name = name
        .replace(/[^\w\s]/g, '') // Remove all non-alphanumeric characters (punctuation)
        .replace(/\s+/g, ' ')    // Replace multiple spaces with a single space
        .trim()                 // Trim leading and trailing spaces
        .toLowerCase();         // Convert to lowercase

    // Extract the "Class of" year
    const classOfMatch = name.match(/\bclass of (\d{4})\b/);
    const classOfYear = classOfMatch ? classOfMatch[1] : null;

    // Remove the "Class of" part from the name
    name = name.replace(/\bclass of \d{4}\b/g, '').trim();

    // Handle names with commas (e.g., "Tserakhava, Anastasiya")
    if (name.includes(',')) {
        const [lastName, firstName] = name.split(',').map(part => part.trim());
        name = `${firstName} ${lastName}`; // Reorder to "FirstName LastName"
    } else {
        // Handle names without commas (e.g., "Tserakhava Anastasiya")
        const parts = name.split(' ');
        if (parts.length >= 2) {
            const lastName = parts[0]; // First part is the last name
            const firstName = parts.slice(1).join(' '); // Rest is the first name
            name = `${firstName} ${lastName}`; // Reorder to "FirstName LastName"
        }
    }

    // Add the "Class of" year back to the name
    if (classOfYear) {
        name = `${name} class of ${classOfYear}`;
    }

    return name;
}

function formatNameForDisplay(name) {
    return name
        .split(' ') // Split the name into words
        .map(word => word.charAt(0).toUpperCase() + word.slice(1)) // Capitalize the first letter of each word
        .join(' '); // Rejoin the words into a single string
}

// AddTreeForm Component
function AddTreeForm({ onClose, onSubmit }) {
    const [species, setSpecies] = useState('');
    const [remarks, setRemarks] = useState('');
    const [treeImage, setTreeImage] = useState(null);
    const [gpsCoordinates, setGpsCoordinates] = useState(null);

    const handleImageUpload = (e) => {
        const file = e.target.files[0];
        if (file) {
            const reader = new FileReader();
            reader.onloadend = () => {
                setTreeImage(reader.result);
            };
            reader.readAsDataURL(file);
        }
    };

    const handleGetLocation = () => {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                (position) => {
                    const { latitude, longitude } = position.coords;
                    setGpsCoordinates(`${latitude}, ${longitude}`);
                },
                (error) => {
                    console.error('Error getting location:', error);
                    alert('Unable to retrieve your location.');
                }
            );
        } else {
            alert('Geolocation is not supported by your browser.');
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!species || !remarks || !treeImage || !gpsCoordinates) {
            alert('Please fill out all fields and upload an image.');
            return;
        }
        onSubmit({ species, remarks, treeImage, gpsCoordinates });
    };

    return (
        <div style={{ marginTop: '20px', padding: '20px', border: '1px solid #ccc' }}>
            <h2>Add a New Tree</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Species:</label>
                    <input
                        type="text"
                        value={species}
                        onChange={(e) => setSpecies(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Remarks:</label>
                    <input
                        type="text"
                        value={remarks}
                        onChange={(e) => setRemarks(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Tree Image:</label>
                    <input
                        type="file"
                        accept="image/*"
                        onChange={handleImageUpload}
                        required
                    />
                </div>
                <div>
                    <label>GPS Coordinates:</label>
                    <button type="button" onClick={handleGetLocation}>
                        Get Location
                    </button>
                    {gpsCoordinates && <span>{gpsCoordinates}</span>}
                </div>
                <button type="submit">Submit</button>
                <button type="button" onClick={onClose}>Cancel</button>
            </form>
        </div>
    );
}

function App() {
    const [name, setName] = useState('');
    const [data, setData] = useState(null);
    const [error, setError] = useState('');
    const [user, setUser] = useState(null); // Firebase user state
    const [showAddTreeForm, setShowAddTreeForm] = useState(false); // State for AddTreeForm

    // Handle sign-up
    const handleSignUp = async (email, password, firstName, secondName, year) => {
        try {
            const userCredential = await createUserWithEmailAndPassword(auth, email, password);
            const user = userCredential.user;

            // Combine first name, second name, and year to create the full name
            const fullName = `${firstName} ${secondName} - Class of ${year}`;
            const normalizedName = normalizeName(fullName); // Normalize the name
            setName(normalizedName); // Store the normalized name in local state
            setUser(user);
        } catch (err) {
            console.error('Sign-up failed:', err);
            setError('Sign-up failed. Please try again.');
        }
    };

    const handleLogin = async (email, password, firstName, secondName, year) => {
        try {
            const userCredential = await signInWithEmailAndPassword(auth, email, password);
            const user = userCredential.user;

            // Combine first name, second name, and year to create the full name
            const fullName = `${firstName} ${secondName} - Class of ${year}`;
            const normalizedName = normalizeName(fullName); // Normalize the name
            setName(normalizedName); // Store the normalized name in local state
            setUser(user);
        } catch (err) {
            console.error('Login failed:', err);
            setError('Login failed. Please check your credentials.');
        }
    };

    // Handle logout
    const handleLogout = async () => {
        try {
            await signOut(auth);
            setUser(null);
            setName('');
            setData(null);
        } catch (err) {
            console.error('Logout failed:', err);
            setError('Logout failed. Please try again.');
        }
    };

    // Fetch data when name changes
    useEffect(() => {
        if (name) {
            const fetchData = async () => {
                try {
                    console.log('Searching for name:', name); // Log the name being searched

                    // Normalize the user's input before sending it to the server
                    const normalizedUserName = normalizeName(name);

                    const response = await axios.post('http://localhost:5000/api/search', { name: normalizedUserName });
                    console.log('Response:', response.data); // Log the response
                    setData(response.data);
                    setError('');
                } catch (err) {
                    console.error('Error fetching data:', err.response ? err.response.data : err.message); // Log the error
                    setError('Name not found');
                    setData(null);
                }
            };
            fetchData();
        }
    }, [name]);

    // Handle adding a new tree
    const handleAddTree = async (treeData) => {
        try {
            const response = await axios.post('http://localhost:5000/api/add-tree', {
                ...treeData,
                name: name, // Include the user's name
            });
            console.log('Tree added:', response.data);
            setShowAddTreeForm(false);
            // Refresh the data
            const fetchResponse = await axios.post('http://localhost:5000/api/search', { name });
            setData(fetchResponse.data);
        } catch (err) {
            console.error('Error adding tree:', err);
            alert('Failed to add tree. Please try again.');
        }
    };

    return (
        <div style={{ padding: '20px' }}>
            <h1>Torque Experiment Data</h1>

            {!user ? (
                <>
                    <SignUpForm onSignUp={handleSignUp} error={error} />
                    <LoginForm onLogin={handleLogin} error={error} />
                </>
            ) : (
                <div>
                    <p>Welcome, {formatNameForDisplay(name)}!</p>
                    <button onClick={handleLogout}>Logout</button>

                    {/* Add Tree Button and Form */}
                    <button onClick={() => setShowAddTreeForm(true)}>Add Tree</button>

                    {showAddTreeForm && (
                        <AddTreeForm
                            onClose={() => setShowAddTreeForm(false)}
                            onSubmit={handleAddTree}
                        />
                    )}

                    {data && (
                        <div>
                            <h2>Data for {formatNameForDisplay(data.name)}</h2>
                            <table border="1" cellPadding="10" style={{ marginTop: '20px' }}>
                                <thead>
                                    <tr>
                                        <th>#</th>
                                        <th>Date</th>
                                        <th>Location</th>
                                        <th>GPS Coordinates</th>
                                        <th>Type of Activity</th>
                                        <th>Species</th>
                                        <th>Remarks</th>
                                        <th>Tree Image</th> {/* Add Tree Image Column */}
                                    </tr>
                                </thead>
                                <tbody>
                                    {data.results.map((row, index) => (
                                        <tr key={index}>
                                            <td>{row.num}</td>
                                            <td>{row.date}</td>
                                            <td>{row.location}</td>
                                            <td>{row.gpsCoordinates}</td>
                                            <td>{row.typeOfActivity}</td>
                                            <td>{row.species}</td>
                                            <td>{row.remarks}</td>
                                            <td>
                                                {row.treeImage && (
                                                    <img src={row.treeImage} alt="Tree" style={{ width: '100px', height: 'auto' }} />
                                                )}
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
}

// Sign-Up Form Component
function SignUpForm({ onSignUp, error }) {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [firstName, setFirstName] = useState('');
    const [secondName, setSecondName] = useState('');
    const [year, setYear] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        onSignUp(email, password, firstName, secondName, year);
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>Sign Up</h2>
            <div>
                <input
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />
            </div>
            <div>
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
            </div>
            <div>
                <input
                    type="text"
                    placeholder="First Name"
                    value={firstName}
                    onChange={(e) => setFirstName(e.target.value)}
                    required
                />
            </div>
            <div>
                <input
                    type="text"
                    placeholder="Second Name"
                    value={secondName}
                    onChange={(e) => setSecondName(e.target.value)}
                    required
                />
            </div>
            <div>
                <input
                    type="text"
                    placeholder="Year (e.g., 2026)"
                    value={year}
                    onChange={(e) => setYear(e.target.value)}
                    required
                />
            </div>
            <button type="submit">Sign Up</button>
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </form>
    );
}

// Login Form Component
function LoginForm({ onLogin, error }) {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [firstName, setFirstName] = useState('');
    const [secondName, setSecondName] = useState('');
    const [year, setYear] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        onLogin(email, password, firstName, secondName, year);
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>Login</h2>
            <div>
                <input
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />
            </div>
            <div>
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
            </div>
            <div>
                <input
                    type="text"
                    placeholder="First Name"
                    value={firstName}
                    onChange={(e) => setFirstName(e.target.value)}
                    required
                />
            </div>
            <div>
                <input
                    type="text"
                    placeholder="Second Name"
                    value={secondName}
                    onChange={(e) => setSecondName(e.target.value)}
                    required
                />
            </div>
            <div>
                <input
                    type="text"
                    placeholder="Year (e.g., 2026)"
                    value={year}
                    onChange={(e) => setYear(e.target.value)}
                    required
                />
            </div>
            <button type="submit">Login</button>
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </form>
    );
}

export default App;
