import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { signInWithEmailAndPassword, createUserWithEmailAndPassword, signOut } from 'firebase/auth';
import { auth } from './firebase';

function App() {
    const [name, setName] = useState('');
    const [data, setData] = useState(null);
    const [error, setError] = useState('');
    const [user, setUser] = useState(null); // Firebase user state

    // Handle sign-up
    const handleSignUp = async (email, password, firstName, secondName, year) => {
        try {
            const userCredential = await createUserWithEmailAndPassword(auth, email, password);
            const user = userCredential.user;

            // Combine first name, second name, and year to create the full name
            const fullName = `${firstName} ${secondName} - Class of ${year}`;
            setName(fullName); // Store the name in local state
            setUser(user);
        } catch (err) {
            console.error('Sign-up failed:', err);
            setError('Sign-up failed. Please try again.');
        }
    };

    // Handle login
    const handleLogin = async (email, password) => {
        try {
            const userCredential = await signInWithEmailAndPassword(auth, email, password);
            const user = userCredential.user;

            // For simplicity, assume the name is stored in the user's email (e.g., "anastasiya@example.com" -> "Anastasiya")
            const fullName = email.split('@')[0] + ' - Class of 2026'; // Example logic
            setName(fullName); // Store the name in local state
            setUser(user);
        } catch (err) {
            console.error('Login failed:', err);
            setError('Login failed. Please check your credentials.');
        }
    };

    // Handle logout
    const handleLogout = async () => {
        try {
            await signOut(auth);
            setUser(null);
            setName('');
            setData(null);
        } catch (err) {
            console.error('Logout failed:', err);
            setError('Logout failed. Please try again.');
        }
    };

    // Fetch data when name changes
    useEffect(() => {
        if (name) {
            const fetchData = async () => {
                try {
                    const response = await axios.post('http://localhost:5000/api/search', { name });
                    setData(response.data);
                    setError('');
                } catch (err) {
                    setError('Name not found');
                    setData(null);
                }
            };
            fetchData();
        }
    }, [name]);

    return (
        <div style={{ padding: '20px' }}>
            <h1>Torque Experiment Data</h1>

            {!user ? (
                <>
                    <SignUpForm onSignUp={handleSignUp} error={error} />
                    <LoginForm onLogin={handleLogin} error={error} />
                </>
            ) : (
                <div>
                    <p>Welcome, {name}!</p>
                    <button onClick={handleLogout}>Logout</button>

                    {data && (
                        <div>
                            <h2>Data for {data.name}</h2>
                            <table border="1" cellPadding="10" style={{ marginTop: '20px' }}>
                                <thead>
                                    <tr>
                                        <th>#</th>
                                        <th>Date</th>
                                        <th>Location</th>
                                        <th>GPS Coordinates</th>
                                        <th>Type of Activity</th>
                                        <th>Species</th>
                                        <th>Remarks</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {data.results.map((row, index) => (
                                        <tr key={index}>
                                            <td>{row.num}</td>
                                            <td>{row.date}</td>
                                            <td>{row.location}</td>
                                            <td>{row.gpsCoordinates}</td>
                                            <td>{row.typeOfActivity}</td>
                                            <td>{row.species}</td>
                                            <td>{row.remarks}</td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
}

// Sign-Up Form Component
function SignUpForm({ onSignUp, error }) {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [firstName, setFirstName] = useState('');
    const [secondName, setSecondName] = useState('');
    const [year, setYear] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        onSignUp(email, password, firstName, secondName, year);
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>Sign Up</h2>
            <div>
                <input
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />
            </div>
            <div>
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
            </div>
            <div>
                <input
                    type="text"
                    placeholder="First Name"
                    value={firstName}
                    onChange={(e) => setFirstName(e.target.value)}
                    required
                />
            </div>
            <div>
                <input
                    type="text"
                    placeholder="Second Name"
                    value={secondName}
                    onChange={(e) => setSecondName(e.target.value)}
                    required
                />
            </div>
            <div>
                <input
                    type="text"
                    placeholder="Year (e.g., 2026)"
                    value={year}
                    onChange={(e) => setYear(e.target.value)}
                    required
                />
            </div>
            <button type="submit">Sign Up</button>
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </form>
    );
}

// Login Form Component
function LoginForm({ onLogin, error }) {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        onLogin(email, password);
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>Login</h2>
            <div>
                <input
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />
            </div>
            <div>
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
            </div>
            <button type="submit">Login</button>
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </form>
    );
}

export default App;
