import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { signInWithEmailAndPassword, createUserWithEmailAndPassword, signOut } from 'firebase/auth';
import { auth } from './firebase';

// Normalization function
function normalizeName(name) {
    // Remove "Class of" and parentheses
    name = name.replace(/\(Class of \d{4}\)/g, '').trim();

    // Handle names with commas (e.g., "Mathur, Arnav")
    if (name.includes(',')) {
        const [surname, firstName] = name.split(',').map(part => part.trim());
        return `${firstName} ${surname}`.toLowerCase().trim();
    }

    // Handle names without commas (e.g., "Kuldeep Khoont")
    return name.toLowerCase().trim();
}
app.post('/api/search', async (req, res) => {
  const { name } = req.body;
  console.log(`Searching for name: "${name}"`); // Log the name being searched

  try {
      const data = await getSheetData('MasterSheet');
      console.log('Fetched data:', data);

      // Normalize the user's name
      const normalizedUserName = normalizeName(name);

      // Skip the first row (headers) and filter rows that match the normalized name
      const matchingRows = data.slice(1).filter(row => {
          const rowName = row[7]; // Assuming 'name' is in the 8th column (index 7)
          if (!rowName) {
              console.warn('Row with missing name:', row);
              return false;
          }

          // Normalize the name from the sheet
          const normalizedRowName = normalizeName(rowName);
          console.log(`Comparing "${normalizedRowName}" with "${normalizedUserName}"`); // Log comparison
          return normalizedRowName === normalizedUserName;
      });

      console.log('Matching rows:', matchingRows);

      if (matchingRows.length > 0) {
          const results = matchingRows.map(row => ({
              num: row[0],           // num
              date: row[1],          // Date
              location: row[2],      // Location
              gpsCoordinates: row[3], // GPS Coordinates
              typeOfActivity: row[4], // Type of Activity
              species: row[5],       // Species
              remarks: row[6],       // Remarks
              name: row[7],          // name
          }));
          res.json({ name, results });
      } else {
          res.status(404).json({ error: 'Name not found' });
      }
  } catch (err) {
      console.error('Error in /api/search:', err);
      res.status(500).json({ error: 'Failed to fetch data' });
  }
});
function App() {
    const [name, setName] = useState('');
    const [data, setData] = useState(null);
    const [error, setError] = useState('');
    const [user, setUser] = useState(null); // Firebase user state

    // Handle sign-up
    const handleSignUp = async (email, password, firstName, secondName, year) => {
      try {
          const userCredential = await createUserWithEmailAndPassword(auth, email, password);
          const user = userCredential.user;

          // Combine first name, second name, and year to create the full name
          const fullName = `${firstName} ${secondName} - Class of ${year}`;
          const normalizedName = normalizeName(fullName); // Normalize the name
          setName(normalizedName); // Store the normalized name in local state
          setUser(user);
      } catch (err) {
          console.error('Sign-up failed:', err);
          setError('Sign-up failed. Please try again.');
      }
  };

  const handleLogin = async (email, password, firstName, secondName, year) => {
      try {
          const userCredential = await signInWithEmailAndPassword(auth, email, password);
          const user = userCredential.user;

          // Combine first name, second name, and year to create the full name
          const fullName = `${firstName} ${secondName} - Class of ${year}`;
          const normalizedName = normalizeName(fullName); // Normalize the name
          setName(normalizedName); // Store the normalized name in local state
          setUser(user);
      } catch (err) {
          console.error('Login failed:', err);
          setError('Login failed. Please check your credentials.');
      }
  };

    // Handle logout
    const handleLogout = async () => {
        try {
            await signOut(auth);
            setUser(null);
            setName('');
            setData(null);
        } catch (err) {
            console.error('Logout failed:', err);
            setError('Logout failed. Please try again.');
        }
    };

    // Fetch data when name changes
    useEffect(() => {
        if (name) {
          const fetchData = async () => {
            try {
                console.log('Searching for name:', name); // Log the name being searched
                const response = await axios.post('http://localhost:5000/api/search', { name });
                console.log('Response:', response.data); // Log the response
                setData(response.data);
                setError('');
            } catch (err) {
                console.error('Error fetching data:', err.response ? err.response.data : err.message); // Log the error
                setError('Name not found');
                setData(null);
            }
        };
            fetchData();
        }
    }, [name]);

    return (
        <div style={{ padding: '20px' }}>
            <h1>Torque Experiment Data</h1>

            {!user ? (
                <>
                    <SignUpForm onSignUp={handleSignUp} error={error} />
                    <LoginForm onLogin={handleLogin} error={error} />
                </>
            ) : (
                <div>
                    <p>Welcome, {name}!</p>
                    <button onClick={handleLogout}>Logout</button>

                    {data && (
                        <div>
                            <h2>Data for {data.name}</h2>
                            <table border="1" cellPadding="10" style={{ marginTop: '20px' }}>
                                <thead>
                                    <tr>
                                        <th>#</th>
                                        <th>Date</th>
                                        <th>Location</th>
                                        <th>GPS Coordinates</th>
                                        <th>Type of Activity</th>
                                        <th>Species</th>
                                        <th>Remarks</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {data.results.map((row, index) => (
                                        <tr key={index}>
                                            <td>{row.num}</td>
                                            <td>{row.date}</td>
                                            <td>{row.location}</td>
                                            <td>{row.gpsCoordinates}</td>
                                            <td>{row.typeOfActivity}</td>
                                            <td>{row.species}</td>
                                            <td>{row.remarks}</td>
