import axios from 'axios';

export const searchByName = async (name) => {
    try {
        const response = await axios.post('http://localhost:5000/api/search', { name });
        return response.data;
    } catch (err) {
        console.error('Error fetching data:', err.response ? err.response.data : err.message);
        throw new Error('Name not found');
    }
};

export const addTree = async (treeData) => {
    try {
        const response = await axios.post('http://localhost:5000/api/add-tree', treeData);
        return response.data;
    } catch (err) {
        console.error('Error adding tree:', err);
        throw new Error('Failed to add tree. Please try again.');
    }
};
