import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { signInWithEmailAndPassword, createUserWithEmailAndPassword, signOut, onAuthStateChanged } from 'firebase/auth';
import { doc, setDoc, getDoc } from 'firebase/firestore';
import { auth, db } from './firebase';

function App() {
    const [name, setName] = useState('');
    const [data, setData] = useState(null);
    const [error, setError] = useState('');
    const [user, setUser] = useState(null); // Firebase user state

    // Check authentication state on app load
    useEffect(() => {
        const unsubscribe = onAuthStateChanged(auth, async (user) => {
            if (user) {
                setUser(user);

                // Fetch the name from Firestore
                const userDoc = await getDoc(doc(db, 'users', user.uid));
                if (userDoc.exists()) {
                    setName(userDoc.data().name);
                } else {
                    setError('User data not found.');
                }
            } else {
                setUser(null);
                setName('');
                setData(null);
            }
        });

        return () => unsubscribe(); // Cleanup subscription
    }, []);

    // Handle sign-up
    const handleSignUp = async (email, password, name) => {
        try {
            const userCredential = await createUserWithEmailAndPassword(auth, email, password);
            const user = userCredential.user;

            // Store the name in Firestore
            await setDoc(doc(db, 'users', user.uid), {
                name: name,
            });

            setUser(user);
            setName(name);
        } catch (err) {
            setError('Sign-up failed. Please try again.');
        }
    };

    // Handle login
    const handleLogin = async (email, password) => {
        try {
            const userCredential = await signInWithEmailAndPassword(auth, email, password);
            const user = userCredential.user;

            // Fetch the name from Firestore
            const userDoc = await getDoc(doc(db, 'users', user.uid));
            if (userDoc.exists()) {
                setName(userDoc.data().name);
            } else {
                setError('User data not found.');
            }

            setUser(user);
        } catch (err) {
            setError('Login failed. Please check your credentials.');
        }
    };

    // Handle logout
    const handleLogout = async () => {
        try {
            await signOut(auth);
            setUser(null);
            setName('');
            setData(null);
        } catch (err) {
            setError('Logout failed. Please try again.');
        }
    };

    // Fetch data when name changes
    useEffect(() => {
        if (name) {
            const fetchData = async () => {
                try {
                    const response = await axios.post('http://localhost:5000/api/search', { name });
                    setData(response.data);
                    setError('');
                } catch (err) {
                    setError('Name not found');
                    setData(null);
                }
            };
            fetchData();
        }
    }, [name]);

    return (
        <div style={{ padding: '20px' }}>
            <h1>Torque Experiment Data</h1>

            {!user ? (
                <>
                    <SignUpForm onSignUp={handleSignUp} error={error} />
                    <LoginForm onLogin={handleLogin} error={error} />
                </>
            ) : (
                <div>
                    <p>Welcome, {name}!</p>
                    <button onClick={handleLogout}>Logout</button>

                    {data && (
                        <div>
                            <h2>Data for {data.name}</h2>
                            <table border="1" cellPadding="10" style={{ marginTop: '20px' }}>
                                <thead>
                                    <tr>
                                        <th>#</th>
                                        <th>lw, cm</th>
                                        <th>lp, cm</th>
                                        <th>trial 1</th>
                                        <th>trial 2</th>
                                        <th>trial 3</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {data.results.map((row, index) => (
                                        <tr key={index}>
                                            <td>{index + 1}</td>
                                            <td>{row.lw}</td>
                                            <td>{row.lp}</td>
                                            <td>{row.trial1}</td>
                                            <td>{row.trial2}</td>
                                            <td>{row.trial3}</td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
}

// Sign-Up Form Component
function SignUpForm({ onSignUp, error }) {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [name, setName] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        onSignUp(email, password, name);
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>Sign Up</h2>
            <div>
                <input
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />
            </div>
            <div>
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
            </div>
            <div>
                <input
                    type="text"
                    placeholder="Name"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    required
                />
            </div>
            <button type="submit">Sign Up</button>
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </form>
    );
}

// Login Form Component
function LoginForm({ onLogin, error }) {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        onLogin(email, password);
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>Login</h2>
            <div>
                <input
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />
            </div>
            <div>
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
            </div>
            <button type="submit">Login</button>
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </form>
    );
}

export default App;
