import React, { useState, useEffect } from 'react';
import axios from 'axios';
import imageCompression from 'browser-image-compression';
import { signInWithEmailAndPassword, createUserWithEmailAndPassword, signOut } from 'firebase/auth';
import { auth } from './firebase';

// Utility functions
function normalizeName(name) {
    name = name
        .replace(/[^\w\s]/g, '')
        .replace(/\s+/g, ' ')
        .trim()
        .toLowerCase();

    const classOfMatch = name.match(/\bclass of (\d{4})\b/);
    const classOfYear = classOfMatch ? classOfMatch[1] : null;

    name = name.replace(/\bclass of \d{4}\b/g, '').trim();

    if (name.includes(',')) {
        const [lastName, firstName] = name.split(',').map(part => part.trim());
        name = `${firstName} ${lastName}`;
    } else {
        const parts = name.split(' ');
        if (parts.length >= 2) {
            const lastName = parts[0];
            const firstName = parts.slice(1).join(' ');
            name = `${firstName} ${lastName}`;
        }
    }

    if (classOfYear) {
        name = `${name} class of ${classOfYear}`;
    }

    return name;
}

function formatNameForDisplay(name) {
    return name
        .split(' ')
        .map(word => word.charAt(0).toUpperCase() + word.slice(1))
        .join(' ');
}

// AddTreeForm Component
function AddTreeForm({ onClose, onSubmit }) {
    const [species, setSpecies] = useState('');
    const [remarks, setRemarks] = useState('');
    const [treeImage, setTreeImage] = useState(null);
    const [gpsCoordinates, setGpsCoordinates] = useState(null);

    const handleImageUpload = async (e) => {
        const file = e.target.files[0];
        if (file) {
            try {
                const options = {
                    maxSizeMB: 1,
                    maxWidthOrHeight: 1024,
                    useWebWorker: true,
                };
                const compressedFile = await imageCompression(file, options);
                const reader = new FileReader();
                reader.onloadend = () => {
                    setTreeImage(reader.result);
                };
                reader.readAsDataURL(compressedFile);
            } catch (error) {
                console.error('Error compressing image:', error);
                alert('Failed to compress image. Please try again.');
            }
        }
    };

    const handleGetLocation = () => {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                (position) => {
                    const { latitude, longitude } = position.coords;
                    setGpsCoordinates(`${latitude}, ${longitude}`);
                },
                (error) => {
                    console.error('Error getting location:', error);
                    switch (error.code) {
                        case error.PERMISSION_DENIED:
                            alert('Location access was denied. Please enable location permissions in your browser settings.');
                            break;
                        case error.POSITION_UNAVAILABLE:
                            alert('Location information is unavailable. Please check your device settings.');
                            break;
                        case error.TIMEOUT:
                            alert('The request to get your location timed out. Please try again.');
                            break;
                        default:
                            alert('An unknown error occurred while trying to get your location.');
                            break;
                    }
                },
                {
                    enableHighAccuracy: true,
                    timeout: 10000,
                    maximumAge: 0,
                }
            );
        } else {
            alert('Geolocation is not supported by your browser. Please use a modern browser.');
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!species || !remarks || !treeImage || !gpsCoordinates) {
            alert('Please fill out all fields and upload an image.');
            return;
        }
        onSubmit({ species, remarks, treeImage, gpsCoordinates });
    };

    return (
        <div style={{ marginTop: '20px', padding: '20px', border: '1px solid #ccc' }}>
            <h2>Add a New Tree</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Species:</label>
                    <input
                        type="text"
                        value={species}
                        onChange={(e) => setSpecies(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Remarks:</label>
                    <input
                        type="text"
                        value={remarks}
                        onChange={(e) => setRemarks(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Tree Image:</label>
                    <input
                        type="file"
                        accept="image/*"
                        onChange={handleImageUpload}
                        required
                    />
                </div>
                <div>
                    <label>GPS Coordinates:</label>
                    <button type="button" onClick={handleGetLocation}>
                        Get Location
                    </button>
                    {gpsCoordinates && <span>{gpsCoordinates}</span>}
                </div>
                <button type="submit">Submit</button>
                <button type="button" onClick={onClose}>Cancel</button>
            </form>
        </div>
    );
}

// AuthForm Component (Reusable for Login and Sign-Up)
function AuthForm({ type, onSubmit, error }) {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [firstName, setFirstName] = useState('');
    const [secondName, setSecondName] = useState('');
    const [year, setYear] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        onSubmit(email, password, firstName, secondName, year);
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>{type === 'login' ? 'Login' : 'Sign Up'}</h2>
            <div>
                <input
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />
            </div>
            <div>
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
            </div>
            <div>
                <input
                    type="text"
                    placeholder="First Name"
                    value={firstName}
                    onChange={(e) => setFirstName(e.target.value)}
                    required
                />
            </div>
            <div>
                <input
                    type="text"
                    placeholder="Second Name"
                    value={secondName}
                    onChange={(e) => setSecondName(e.target.value)}
                    required
                />
            </div>
            <div>
                <input
                    type="text"
                    placeholder="Year (e.g., 2026)"
                    value={year}
                    onChange={(e) => setYear(e.target.value)}
                    required
                />
            </div>
            <button type="submit">{type === 'login' ? 'Login' : 'Sign Up'}</button>
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </form>
    );
}

// Main App Component
function App() {
    const [name, setName] = useState('');
    const [data, setData] = useState(null);
    const [error, setError] = useState('');
    const [user, setUser] = useState(null);
    const [showAddTreeForm, setShowAddTreeForm] = useState(false);

    const handleSignUp = async (email, password, firstName, secondName, year) => {
        try {
            const userCredential = await createUserWithEmailAndPassword(auth, email, password);
            const user = userCredential.user;
            const fullName = `${firstName} ${secondName} - Class of ${year}`;
            const normalizedName = normalizeName(fullName);
            setName(normalizedName);
            setUser(user);
        } catch (err) {
            console.error('Sign-up failed:', err);
            setError('Sign-up failed. Please try again.');
        }
    };

    const handleLogin = async (email, password, firstName, secondName, year) => {
        try {
            const userCredential = await signInWithEmailAndPassword(auth, email, password);
            const user = userCredential.user;
            const fullName = `${firstName} ${secondName} - Class of ${year}`;
            const normalizedName = normalizeName(fullName);
            setName(normalizedName);
            setUser(user);
        } catch (err) {
            console.error('Login failed:', err);
            setError('Login failed. Please check your credentials.');
        }
    };

    const handleLogout = async () => {
        try {
            await signOut(auth);
            setUser(null);
            setName('');
            setData(null);
        } catch (err) {
            console.error('Logout failed:', err);
            setError('Logout failed. Please try again.');
        }
    };

    useEffect(() => {
        if (name) {
            const fetchData = async () => {
                try {
                    const normalizedUserName = normalizeName(name);
                    const response = await axios.post('http://localhost:5000/api/search', { name: normalizedUserName });
                    setData(response.data);
                    setError('');
                } catch (err) {
                    console.error('Error fetching data:', err.response ? err.response.data : err.message);
                    setError('Name not found');
                    setData(null);
                }
            };
            fetchData();
        }
    }, [name]);

    const handleAddTree = async (treeData) => {
        try {
            const response = await axios.post('http://localhost:5000/api/add-tree', {
                ...treeData,
                name: name,
            });
            console.log('Tree added:', response.data);
            setShowAddTreeForm(false);
            const fetchResponse = await axios.post('http://localhost:5000/api/search', { name });
            setData(fetchResponse.data);
        } catch (err) {
            console.error('Error adding tree:', err);
            alert('Failed to add tree. Please try again.');
        }
    };

    return (
        <div style={{ padding: '20px' }}>
            <h1>Torque Experiment Data</h1>

            {!user ? (
                <>
                    <AuthForm type="signup" onSubmit={handleSignUp} error={error} />
                    <AuthForm type="login" onSubmit={handleLogin} error={error} />
                </>
            ) : (
                <div>
                    <p>Welcome, {formatNameForDisplay(name)}!</p>
                    <button onClick={handleLogout}>Logout</button>
                    <button onClick={() => setShowAddTreeForm(true)}>Add Tree</button>

                    {showAddTreeForm && (
                        <AddTreeForm
                            onClose={() => setShowAddTreeForm(false)}
                            onSubmit={handleAddTree}
                        />
                    )}

                    {data && (
                        <div>
                            <h2>Data for {formatNameForDisplay(data.name)}</h2>
                            <table border="1" cellPadding="10" style={{ marginTop: '20px' }}>
                                <thead>
                                    <tr>
                                        <th>#</th>
                                        <th>Date</th>
                                        <th>Location</th>
                                        <th>GPS Coordinates</th>
                                        <th>Type of Activity</th>
                                        <th>Species</th>
                                        <th>Remarks</th>
                                        <th>Tree Image</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {data.results.map((row, index) => (
                                        <tr key={index}>
                                            <td>{row.num}</td>
                                            <td>{row.date}</td>
                                            <td>{row.location}</td>
                                            <td>{row.gpsCoordinates}</td>
                                            <td>{row.typeOfActivity}</td>
                                            <td>{row.species}</td>
                                            <td>{row.remarks}</td>
                                            <td>
                                                {row.treeImage && (
                                                    <img src={row.treeImage} alt="Tree" style={{ width: '100px', height: 'auto' }} />
                                                )}
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
}

export default App;
