import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { signInWithEmailAndPassword, createUserWithEmailAndPassword, signOut } from 'firebase/auth';
import { auth } from './firebase/firebase-client';
import AddTreeForm from './components/AddTreeForm';
import { normalizeName } from './utils/nameUtils';

function formatNameForDisplay(name) {
    return name
        .split(' ')
        .map(word => word.charAt(0).toUpperCase() + word.slice(1))
        .join(' ');
}

function App() {
    const [name, setName] = useState('');
    const [data, setData] = useState(null);
    const [error, setError] = useState('');
    const [user, setUser] = useState(null);

    const handleSignUp = async (email, password, firstName, secondName, year) => {
        try {
            const userCredential = await createUserWithEmailAndPassword(auth, email, password);
            const user = userCredential.user;
            const fullName = `${firstName} ${secondName} - Class of ${year}`;
            const normalizedName = normalizeName(fullName);
            setName(normalizedName);
            setUser(user);
        } catch (err) {
            console.error('Sign-up failed:', err);
            setError('Sign-up failed. Please try again.');
        }
    };

    const handleLogin = async (email, password, firstName, secondName, year) => {
        try {
            const userCredential = await signInWithEmailAndPassword(auth, email, password);
            const user = userCredential.user;
            const fullName = `${firstName} ${secondName} - Class of ${year}`;
            const normalizedName = normalizeName(fullName);
            setName(normalizedName);
            setUser(user);
        } catch (err) {
            console.error('Login failed:', err);
            setError('Login failed. Please check your credentials.');
        }
    };

    const handleLogout = async () => {
        try {
            await signOut(auth);
            setUser(null);
            setName('');
            setData(null);
        } catch (err) {
            console.error('Logout failed:', err);
            setError('Logout failed. Please try again.');
        }
    };

    useEffect(() => {
        if (name) {
            const fetchData = async () => {
                try {
                    const token = await user?.getIdToken();
                    const response = await axios.post(
                        'http://localhost:5000/api/trees/search', 
                        { name: name }, 
                        {
                            headers: {
                                'Content-Type': 'application/json',
                                ...(token && { 'Authorization': `Bearer ${token}` })
                            }
                        }
                    );

                    setData(response.data);
                    console.log("Search results:", response.data);
                } catch (error) {
                    console.error('Fetch error:', error);
                }
            };
            fetchData();
        } else {
            setData({ results: [] });
        }
    }, [name, user]);

    const handleTreeAdded = async () => {
        if (name) {
            try {
                const token = await user?.getIdToken();
                const response = await axios.post(
                    'http://localhost:5000/api/trees/search', 
                    { name }, 
                    {
                        headers: {
                            'Content-Type': 'application/json',
                            ...(token && { 'Authorization': `Bearer ${token}` })
                        }
                    }
                );
                setData(response.data);
            } catch (err) {
                console.error('Error refreshing trees:', err);
            }
        }
    };

    return (
        <div style={{ padding: '20px' }}>
            <h1>Tree Tracking App</h1>

            {!user ? (
                <>
                    <AuthForm type="signup" onSubmit={handleSignUp} error={error} />
                    <AuthForm type="login" onSubmit={handleLogin} error={error} />
                </>
            ) : (
                <div>
                    <p>Welcome, {formatNameForDisplay(name)}!</p>
                    <button onClick={handleLogout}>Logout</button>

                    <AddTreeForm user={user} onTreeAdded={handleTreeAdded} />

                    {data && (
                        <div>
                            <h2>My Planted Trees</h2>
                            <table border="1" cellPadding="10" style={{ marginTop: '20px' }}>
                                <thead>
                                    <tr>
                                        <th>#</th>
                                        <th>Date</th>
                                        <th>Location</th>
                                        <th>GPS Coordinates</th>
                                        <th>Type of Activity</th>
                                        <th>Species</th>
                                        <th>Remarks</th>
                                        <th>Tree Image</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {data.results.map((row, index) => (
                                        <tr key={index}>
                                            <td>{row.num}</td>
                                            <td>{row.date}</td>
                                            <td>{row.location}</td>
                                            <td>{row.gpsCoordinates}</td>
                                            <td>{row.typeOfActivity}</td>
                                            <td>{row.species}</td>
                                            <td>{row.remarks}</td>
                                            <td>
                                                {row.treeImage && (
                                                    <img src={row.treeImage} alt="Tree" style={{ width: '100px', height: 'auto' }} />
                                                )}
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
}

// Add AuthForm component definition
function AuthForm({ type, onSubmit, error }) {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [firstName, setFirstName] = useState('');
    const [secondName, setSecondName] = useState('');
    const [year, setYear] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        onSubmit(email, password, firstName, secondName, year);
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>{type === 'login' ? 'Login' : 'Sign Up'}</h2>
            <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
            <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} required />
            {type === 'signup' && (
                <>
                    <input type="text" placeholder="First Name" value={firstName} onChange={(e) => setFirstName(e.target.value)} required />
                    <input type="text" placeholder="Second Name" value={secondName} onChange={(e) => setSecondName(e.target.value)} required />
                    <input type="text" placeholder="Year (e.g., 2026)" value={year} onChange={(e) => setYear(e.target.value)} required />
                </>
            )}
            <button type="submit">{type === 'login' ? 'Login' : 'Sign Up'}</button>
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </form>
    );
}

export default App;
