import React, { useState } from "react";

const AddTreeForm = ({ user }) => {
  const [species, setSpecies] = useState("");
  const [remarks, setRemarks] = useState("");
  const [gpsCoordinates, setGpsCoordinates] = useState("");
  const [treeImage, setTreeImage] = useState(null);
  const [message, setMessage] = useState("");

  // Handles form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!user) {
      setMessage("Please log in to add a tree.");
      return;
    }

    const formData = new FormData();
    formData.append("species", species);
    formData.append("remarks", remarks);
    formData.append("gpsCoordinates", gpsCoordinates);
    formData.append("treeImage", treeImage);
    formData.append("name", `${user.firstName} ${user.lastName} - Class of ${user.classOf}`);

    try {
      const response = await fetch("http://localhost:5000/api/add-tree", {
        method: "POST",
        body: formData,
      });

      const result = await response.json();
      if (result.success) {
        setMessage("Tree added successfully!");
        setSpecies("");
        setRemarks("");
        setGpsCoordinates("");
        setTreeImage(null);
      } else {
        setMessage("Failed to add tree.");
      }
    } catch (error) {
      console.error("Error submitting form:", error);
      setMessage("An error occurred.");
    }
  };

  return (
    <div className="container mt-4">
      <h2>Add a New Tree</h2>
      {message && <p className="alert alert-info">{message}</p>}
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label className="form-label">Species</label>
          <input
            type="text"
            className="form-control"
            value={species}
            onChange={(e) => setSpecies(e.target.value)}
            required
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Remarks</label>
          <input
            type="text"
            className="form-control"
            value={remarks}
            onChange={(e) => setRemarks(e.target.value)}
          />
        </div>

        <div className="mb-3">
          <label className="form-label">GPS Coordinates</label>
          <input
            type="text"
            className="form-control"
            value={gpsCoordinates}
            onChange={(e) => setGpsCoordinates(e.target.value)}
            required
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Tree Image</label>
          <input
            type="file"
            className="form-control"
            onChange={(e) => setTreeImage(e.target.files[0])}
          />
        </div>

        <button type="submit" className="btn btn-success">
          Submit
        </button>
      </form>
    </div>
  );
};

export default AddTreeForm;
